	public WebElement getDocElement(String docElementId) {
		return(driver.findElement(By.id(docElementId)));
	}
	private WebElement getDocElementByName(String docElementId) {
		return(driver.findElement(By.name(docElementId)));
	}
	private WebElement getDocElementByClass(String docElementClass) {
		return(driver.findElement(By.className(docElementClass)));
	}
	private WebElement getDocElementByLinkText(String docElementLinkText) {
		return(driver.findElement(By.linkText(docElementLinkText)));
	}
	private List <WebElement> getDocElementsByClass(String docElementClass) {
		return(driver.findElements(By.className(docElementClass)));
	}
	private void populateDocElement(String docElementId, String charString) throws NoSuchElementException {
		/* IS there a list of the same class doc elements? - like for PIN input */
		List <WebElement> docElemList = getDocElementsByClass(docElementId);
		
		if (docElemList != null && docElemList.size() > 1) {
			String numArr [] = charString.length() > 1 ? charString.split(",") : new String [] {charString}; 
			int count=0;	
			for (WebElement docElem : docElemList) {
					if (!docElem.getDomAttribute("class").endsWith("character-grey-boxes"))
						docElem.sendKeys(numArr[count]);
					count++;
				}
			}else {
				try {
					driver.findElement(By.id(docElementId)).sendKeys(charString);
				}catch (NoSuchElementException nse){
					LOGGER.trace(nse.getStackTrace());
				}
		}
 	}
	
